mapper.select:

select bno,title,content,writer,regDate
from (
	select /*+INDEX_DESC(t_board pk_board) */ 
	rownum rn,title, bno,content,writer,regDate
	from t_board
	where rownum <= #{pageNum} * #{amount}
)
where rn > (#{pageNum}-1) * #{amount}

mapper.update:
UPDATE t_board 
SET title = #{title},
	content = #{content},
	writer = #{writer}
WHERE bno = #{bno}
